eval(postman.getGlobalVariable("payagent-post-request-script"));

pm.test("Response status code is 200", function () {
    pm.response.to.have.status(200);
});

pm.test("TransactionReferenceData contains required fields TransactionId, Token and transactionFollowOnData", function () {
    const responseData = pm.response.json();

    pm.expect(responseData.transactionReferenceData).to.have.property('transactionId').and.to.be.a('string');
    pm.expect(responseData.transactionReferenceData).to.have.property('token').and.to.be.a('string');
    pm.expect(responseData.transactionReferenceData).to.have.property('transactionFollowOnData').and.to.be.a('string');
});

pm.test("AuthorizedAmount, subTotalAmount and totalAmount are non-negative integers", function () {
    const responseData = pm.response.json();

    pm.expect(responseData.transactionResponseData.authorizedAmount).to.be.a('number').and.to.be.at.least(0);
    pm.expect(responseData.transactionResponseData.subTotalAmount).to.be.a('number').and.to.be.at.least(0);
    pm.expect(responseData.transactionResponseData.totalAmount).to.be.a('number').and.to.be.at.least(0);
});
}

